mapper.select:
select distinct CMT_ID, CMT_CONTENT,CMT_WORK,CMT_DATE,CMT_MEMBER
from comments inner Join work on (CMT_WORK = WORK_ID)
              inner join project on (WORK_PROJECT = PROJECT_ID)
              inner join PROJECT_MEMBER on (project_id = PMEMBER_PROJECT)
where PROJECT_ID = #{project_id}

mapper.update:
update COMMENTS
set CMT_CONTENT = #{cmt_content}
where CMT_ID = #{cmt_id}
