mapper.select:
select count (*)
from MESSAGE_MEMBER
where MSG_RMEMBER = #{member_mail}

mapper.update:
update message
set msg_type = 'N'
where msg_id = #{msg_id}

mapper.select:
		select count(*) from message A, message_member B
        where MSG_SMEMBER = #{member_mail}
        and A.MSG_ID = B.MSGMEMBER_MSG

select count(*)
from   FRIENDSLIST 
where  friend_myemail = #{member_mail}
