mapper.update:
update POST set POST_TITLE = #{post_title}, POST_CONTENT = #{post_content},
POST_PUBLIC = 'y' POST_PUBLIC = 'n', POST_PASS = #{post_pass}
where POST_ID = #{post_id}

mapper.select:
    select 
	    POST_TITLE title, 
	    POST_CONTENT, 
	    POST_DATE, 
	    level
	from(
	    select rownum rnum, b.* 
	    from(
	        select a.* 
	        from(
	            select POST.* 
	            from POST 
	            where BOARD_ID = #{board_id}
	            order by POST_ID desc
	            ) a
	        ) b
	    where POST_TITLE like '%'||#{searchText}||'%'
	    )  
	where 
		rnum 
	between ${pageNum} * ${postCnt} - (${postCnt} - 1) 
	and ${pageNum} * ${postCnt}	

	start with POST_HIERARCHY is null 
	connect by prior POST_ID = POST_HIERARCHY
	order siblings by POST_ID desc

select 
    POST_TITLE, 
    POST_CONTENT, 
    POST_DATE
from(
    select rownum rnum, a.* 
    from(
        select POST.* 
        from POST 
        where BOARD_ID= #{board_id}
        and  POST_HIERARCHY is null 
        order by POST_ID desc
        ) a
    )
where 
	rnum 
between ${pageNum} * ${postCnt} - (${postCnt} - 1) 
and ${pageNum} * ${postCnt}

    	SELECT  *
		FROM    (
		        select  rownum rnum,
		                a.*
		        from    (
		                select  a.*
		                from    (
		                        SELECT  ROWNUM labelrn,
		                                A.*
		                        FROM    (
		                                SELECT POST_TITLE, POST_CONTENT, POST_DATE
		                                FROM POST
		                                WHERE BOARD_ID = '1'
		                                ) A
		                        ) a
		                order by labelrn desc
		                ) a
		        ) B
		WHERE   rnum BETWEEN #{page} * #{pageSize} - (#{pageSize} - #{page}) AND #{page} * #{pageSize}
		ORDER BY rnum
