mapper.update:
update PROJECT
set PROJECT_TITLE = #{project_title}, PROJECT_OVERVIEW = #{project_overview},
 PROJECT_BOOKMARK = #{project_bookmark}, PROJECT_EDATE = #{project_edate}
where PROJECT_ID = #{project_id}

mapper.select:
SELECT p.project_id, p.project_title, p.project_sdate, p.project_eedate, p.project_edate, p.project_color
FROM project p WHERE p.project_id = ${pid}
FROM project_member pm INNER JOIN project p ON pm.pmember_project = p.project_id WHERE pm.pmember_member = #{sid}
