mapper.select:
SELECT td.todo_content, td.todo_sdate, td.todo_edate, td.todo_eedate, td.todo_color
FROM project p INNER JOIN work w ON p.project_id = w.work_project INNER JOIN todo td ON w.work_id = td.todo_work WHERE p.project_id = #{pid}
FROM todo td WHERE todo_pmember = #{sid}

SELECT  COUNT(*)
FROM    todo
WHERE   todo_work = #{workVo.work_id}
