mapper.select:
SELECT w.work_title, w.work_sdate, w.work_eedate, w.work_edate, w.work_color
FROM work w WHERE work_project = #{pid}
FROM work_member wm INNER JOIN project_member pm ON wm.pmember_id = pm.pmember_id INNER JOIN work w ON wm.work_id = w.work_id WHERE pm.pmember_member = #{sid}

mapper.update:
UPDATE WORK
SET WORK_TITLE = #{work_title}, WORK_CONTENT = #{work_content}, WORK_TYPE = #{work_type},
 WORK_IMPORTANCE = ${work_importance},  WORK_SDATE = #{work_sdate}, WORK_EEDATE = #{work_eedate},
 WORK_PROGRESS = #{work_progress}, WORK_EDATE = #{work_edate} , WORK_PUBLIC = #{work_public},
 WORK_COLOR = #{work_color}
WHERE work_id = #{work_id}
